parameters.jsCode:
const profileData = $input.item.json;

const fullName = profileData.full_name || profileData.name || 'Unknown';
const nameParts = fullName.split(' ');
const firstName = nameParts[0] || '';
const lastName = nameParts.slice(1).join(' ') || '';

return {
  full_name: fullName,
  first_name: firstName,
  last_name: lastName,
  title: profileData.headline || profileData.title || 'Not specified',
  company: profileData.company || profileData.current_company || 'Unknown',
  location: profileData.location || 'Unknown',
  linkedin_url: $('Split LinkedIn URLs').item.json.linkedin_urls,
  profile_summary: profileData.summary || '',
  experience: profileData.experience || [],
  education: profileData.education || []
};

const emailData = $input.item.json;
const leadData = $('Parse LinkedIn Data').item.json;

const email = emailData.email || emailData.data?.email || null;
const confidence = emailData.confidence || emailData.score || 100;

return {
  ...leadData,
  email: email,
  email_confidence: confidence,
  email_verified: email ? true : false
};

